INSERT INTO `magazine_posts` (`id`, `title`, `thumbnail`, `paragraphs`, `status`, `category_id`, `author_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
('176de9c3-f3b5-4a26-9b5e-308962f4e7ea', 'Liyue tuyệt hảo: Sự tuyệt mỹ của văn hóa Á Đông trong Genshin Impact', 'upload/images/news/1744307595-176de9c3-f3b5-4a26-9b5e-308962f4e7ea.jpeg', '[{\"title\":\"Núi đá sừng sững nơi Liyue\",\"content\":\"                        Liyue nổi bật với những dãy núi đá cao vút, tạo nên cảnh sắc vừa tráng lệ vừa kỳ bí. Các ngọn núi bao phủ bởi làn sương mờ ảo, mang đến một cảm giác như bước vào vùng đất của những huyền thoại. Xen lẫn trong đó là các công trình kiến trúc cổ kính, được chạm khắc tinh xảo từ đá, gợi nhớ đến những câu chuyện thần thoại xưa cũ. Người chơi có thể khám phá các hang động bí ẩn hoặc đứng từ đỉnh núi cao để ngắm nhìn toàn cảnh Liyue, cảm nhận sự bao la và hùng vĩ của vùng đất này.\\r\\n                    \"},{\"title\":\"Cảng Liyue: Nơi giao thương và văn hóa\",\"content\":\"                        Cảng Liyue không chỉ đơn thuần là trung tâm thương mại mà còn là nơi thể hiện rõ nét đặc trưng văn hóa của vùng đất này. Hình ảnh các con thuyền tấp nập, sóng nước dập dềnh dưới ánh hoàng hôn rực rỡ khiến bất kỳ ai cũng cảm nhận được sự sống động và nhộn nhịp. Khu chợ sầm uất với vô số mặt hàng, từ đồ ăn truyền thống đến vật phẩm quý hiếm, làm nổi bật sự phong phú và đa dạng của đời sống nơi đây. Đồng thời, những câu chuyện trao đổi giữa người dân cũng thể hiện sự đoàn kết và ấm cúng của cộng đồng.\\r\\n                    \"},{\"title\":\"Những khu rừng huyền bí và thác nước tuyệt mỹ\",\"content\":\"                        Vùng đất Liyue được bao phủ bởi những khu rừng xanh tươi tốt và hệ thống thác nước kỳ diệu. Rừng tre thanh bình mang đến cảm giác an yên và dịu dàng, là nơi hoàn hảo để người chơi nghỉ chân sau những cuộc hành trình. Trong khi đó, những thác nước lớn nhỏ từ các ngọn núi đổ xuống tạo nên vẻ đẹp vừa mạnh mẽ vừa mềm mại. Ánh sáng phản chiếu qua làn nước tạo ra hiệu ứng lung linh, khiến cảnh sắc trở nên huyền ảo và mê hoặc, đặc biệt là trong ánh sáng buổi bình minh hay hoàng hôn.\\r\\n                    \"},{\"title\":\"Phong cảnh mùa thu tại Liyue\",\"content\":\"                        Mùa thu phủ lên Liyue một vẻ đẹp lãng mạn khó tả với hàng cây phong đỏ rực rỡ, trải dài trên những con đường nhỏ. Lá phong rơi, lấp lánh dưới ánh nắng dịu nhẹ, tạo nên khung cảnh đầy thơ mộng. Người chơi có thể dạo bước qua các ngôi làng nhỏ, cảm nhận không khí trong lành và lắng nghe âm thanh thiên nhiên bình yên. Những cảnh sắc này không chỉ mang lại cảm giác thư thái mà còn giúp người chơi kết nối sâu sắc hơn với thế giới trong trò chơi.\\r\\n                    \"}]', 'public', '32257766-2d1d-4d76-a1ac-e6bce75f3780', '0b2949f2-ca9b-47d9-97d7-2034fc2bcd0c', '2025-04-10 17:53:16', '2025-04-10 22:15:31', NULL),
('20acde91-856b-4385-85cc-40d51fb6ad3d', 'Hành trình kỳ thú trong thế giới tí hon', 'upload/images/news/1744311520-20acde91-856b-4385-85cc-40d51fb6ad3d.jpg', '[{\"title\":\"Mở đầu\",\"content\":\"                        \\\"Những cuộc phiêu lưu của Mít Đặc và các bạn\\\" là một tác phẩm kinh điển dành cho thiếu nhi, được viết bởi nhà văn Nikolai Nosov. Cuốn sách kể về cuộc sống và những chuyến phiêu lưu đầy màu sắc của các nhân vật tí hon trong một thế giới tưởng tượng. Với sự kết hợp giữa trí tưởng tượng phong phú, cốt truyện hấp dẫn và những bài học ý nghĩa, tác phẩm đã trở thành một phần không thể thiếu trong tuổi thơ của nhiều thế hệ độc giả.\\r\\n                    \"},{\"title\":\"Cốt truyện: Hành trình khám phá và trưởng thành\",\"content\":\"                        Câu chuyện xoay quanh Mít Đặc, một cậu bé tí hon nghịch ngợm, thích khoe khoang nhưng lại rất đáng yêu. Cùng với những người bạn như Biết Tuốt, Tịt Mũi, và nhiều nhân vật khác, Mít Đặc đã trải qua những cuộc phiêu lưu thú vị tại thành phố Hoa và các vùng đất khác. Từ việc chế tạo khinh khí cầu để khám phá thế giới, đến những tình huống hài hước và đôi khi nguy hiểm, mỗi chuyến đi đều mang lại những bài học quý giá về tình bạn, sự đoàn kết và lòng dũng cảm. Những tình tiết trong truyện không chỉ khiến người đọc cảm thấy thích thú mà còn khơi gợi trí tưởng tượng phong phú, giúp trẻ em học hỏi và phát triển.\\r\\n                    \"},{\"title\":\"Nhân vật: Sự đa dạng và độc đáo\",\"content\":\"                        Các nhân vật trong truyện được xây dựng với tính cách phong phú và độc đáo. Mít Đặc là trung tâm của câu chuyện với sự nghịch ngợm và sáng tạo, trong khi Biết Tuốt là biểu tượng của trí tuệ và sự điềm tĩnh. Ngoài ra, các nhân vật như bác sĩ Thuốc Viên, thợ máy Bù Loong, hay nhạc sĩ Kèn Đồng đều góp phần làm nên sự sống động và hấp dẫn cho câu chuyện. Mỗi nhân vật không chỉ mang lại tiếng cười mà còn truyền tải những thông điệp ý nghĩa về cuộc sống. Sự đa dạng trong tính cách và nghề nghiệp của các nhân vật tạo nên một bức tranh phong phú về thế giới tí hon, nơi mỗi người đều có vai trò và giá trị riêng.\\r\\n                    \"},{\"title\":\"Thế giới tí hon: Một không gian đầy sáng tạo\",\"content\":\"                        Thế giới trong \\\"Những cuộc phiêu lưu của Mít Đặc và các bạn\\\" được miêu tả với sự sáng tạo và chi tiết đáng kinh ngạc. Từ thành phố Hoa với những con đường nhỏ xinh xắn, đến các khu rừng và cánh đồng rộng lớn, mỗi địa điểm đều mang một vẻ đẹp riêng. Những chi tiết như khinh khí cầu, các công cụ tí hon, hay cách các nhân vật tí hon thực hiện công việc hàng ngày đều khiến người đọc cảm thấy như đang bước vào một thế giới kỳ diệu. Không gian trong truyện không chỉ là nơi diễn ra các cuộc phiêu lưu mà còn là nguồn cảm hứng cho người đọc, khuyến khích họ khám phá và trân trọng vẻ đẹp của cuộc sống.\\r\\n                    \"},{\"title\":\"Thông điệp sâu sắc: Tình bạn và sự đoàn kết\",\"content\":\"                        Dưới lớp vỏ của một câu chuyện phiêu lưu, tác phẩm còn gửi gắm những thông điệp sâu sắc về tình bạn và sự đoàn kết. Các nhân vật tí hon luôn sẵn sàng giúp đỡ nhau vượt qua khó khăn, từ đó xây dựng một cộng đồng gắn bó và yêu thương. Những bài học về lòng dũng cảm, sự sáng tạo và tinh thần trách nhiệm cũng được lồng ghép một cách khéo léo, giúp trẻ em học hỏi và phát triển. Tác phẩm không chỉ là một câu chuyện giải trí mà còn là một nguồn cảm hứng, khuyến khích người đọc sống tích cực và trân trọng những giá trị tốt đẹp trong cuộc sống.\\r\\n                    \"},{\"title\":\"Tầm ảnh hưởng và giá trị lâu dài\",\"content\":\"                        Kể từ khi ra mắt, \\\"Những cuộc phiêu lưu của Mít Đặc và các bạn\\\" đã trở thành một tác phẩm kinh điển, được dịch ra nhiều ngôn ngữ và yêu thích trên toàn thế giới. Tác phẩm không chỉ là một câu chuyện giải trí mà còn là nguồn cảm hứng cho nhiều thế hệ độc giả, khuyến khích họ khám phá, sáng tạo và trân trọng những giá trị tốt đẹp trong cuộc sống. Sự thành công của tác phẩm không chỉ nằm ở cốt truyện hấp dẫn mà còn ở khả năng kết nối với người đọc, khiến họ cảm thấy như đang sống trong thế giới tí hon đầy màu sắc.\\r\\n                    \"},{\"title\":\"Kết.\",\"content\":\"                        \\\"Những cuộc phiêu lưu của Mít Đặc và các bạn\\\" không chỉ là một cuốn sách dành cho thiếu nhi mà còn là một tác phẩm nghệ thuật đầy ý nghĩa. Với cốt truyện hấp dẫn, nhân vật sống động và thông điệp sâu sắc, tác phẩm đã chạm đến trái tim của hàng triệu độc giả. Đây là một cuốn sách không thể bỏ qua, đặc biệt đối với những ai yêu thích sự kỳ diệu và muốn tìm lại những ký ức tuổi thơ. Tác phẩm không chỉ là một câu chuyện mà còn là một hành trình, nơi mỗi người đều có thể tìm thấy một phần của mình trong đó.\\r\\n                    \"}]', 'pending', 'bc5d9a2f-08eb-11f0-a777-fc3497151179', 'd8c80e08-4383-4fa2-83d7-48fb98dfc27f', '2025-04-10 18:58:40', '2025-04-11 17:58:30', NULL),
('2145e19e-59e3-4d76-84a6-bc76ca38e7c9', 'Lạng Sơn - Điểm đến đầy cuốn hút tại vùng Đông Bắc Việt Nam', 'upload/images/news/1743526121-2145e19e-59e3-4d76-84a6-bc76ca38e7c9.jpg', '[{\"title\":\"Lạng Sơn - Vẻ đẹp thiên nhiên và văn hóa đậm đà bản sắc\",\"content\":\"Lạng Sơn, nằm ở vùng Đông Bắc của Việt Nam, là một vùng đất giàu truyền thống văn hóa và lịch sử. Với khí hậu mát mẻ, cảnh sắc thiên nhiên hùng vĩ và con người thân thiện, Lạng Sơn đã trở thành điểm đến hấp dẫn trong lòng nhiều du khách. Đây là nơi hội tụ của những câu chuyện lịch sử hào hùng, những danh lam thắng cảnh tuyệt đẹp, cùng với nền văn hóa đặc sắc của các dân tộc thiểu số. Từ những cánh đồng bát ngát, núi non trùng điệp cho đến các di tích lịch sử và phiên chợ vùng cao đầy màu sắc, Lạng Sơn không chỉ làm say mê những ai yêu thích du lịch khám phá mà còn để lại dấu ấn sâu sắc trong lòng mỗi người ghé thăm.\\r\\n\\r\\nHành trình đến Lạng Sơn không chỉ là hành trình khám phá vẻ đẹp thiên nhiên mà còn là hành trình tìm hiểu về những câu chuyện ẩn sâu trong từng tấc đất. Từ những ngọn núi cao vút, những thung lũng ngập tràn sắc vàng mùa lúa chín, đến các khu chợ nhộn nhịp và những món đặc sản thơm ngon, Lạng Sơn thực sự là viên ngọc quý giữa miền núi Đông Bắc.\"},{\"title\":\"Đỉnh Mẫu Sơn - Nơi chạm vào mây trời\",\"content\":\"Đỉnh Mẫu Sơn nằm cách trung tâm thành phố Lạng Sơn khoảng 30km, là một khu vực với khí hậu ôn hòa, mát lạnh quanh năm. Với độ cao hơn 1.500m so với mực nước biển, Mẫu Sơn mang đến cho du khách cơ hội trải nghiệm không khí trong lành và cảnh quan núi rừng tuyệt đẹp. Đặc biệt, vào mùa đông, đây là một trong số ít nơi tại Việt Nam có tuyết rơi, tạo ra khung cảnh kỳ diệu và hiếm có.\\r\\n\\r\\nDu khách có thể tham gia trekking lên đỉnh núi để ngắm nhìn toàn cảnh vùng Đông Bắc, cảm nhận vẻ đẹp bao la của thiên nhiên, và thưởng thức các món đặc sản địa phương như gà nướng, rượu Mẫu Sơn.\"},{\"title\":\"Thung lũng Bắc Sơn - Bức tranh thiên nhiên thơ mộng\",\"content\":\"Thung lũng Bắc Sơn là một trong những địa điểm nổi bật nhất của Lạng Sơn, nổi tiếng với những cánh đồng lúa chín vàng rực vào mỗi vụ mùa. Được bao quanh bởi các dãy núi đá vôi, thung lũng tạo ra một khung cảnh thơ mộng và yên bình.\\r\\n\\r\\nĐây là nơi lý tưởng để du khách tham gia chụp ảnh, khám phá văn hóa của người dân tộc Tày, và trải nghiệm cuộc sống bình dị. Ngoài ra, từ đỉnh núi Nà Lay, bạn sẽ có cơ hội chiêm ngưỡng toàn cảnh thung lũng Bắc Sơn, đặc biệt là vào lúc bình minh và hoàng hôn.\"},{\"title\":\"Chùa Tam Thanh - Lòng thanh tịnh giữa hang đá\",\"content\":\"Nằm trong hệ thống động Tam Thanh, Chùa Tam Thanh không chỉ là một địa điểm du lịch tâm linh mà còn là một thắng cảnh tự nhiên kỳ thú. Chùa được xây dựng trong hang đá với không gian yên bình và linh thiêng, khiến bất kỳ ai đến đây cũng cảm nhận được sự thư thái trong tâm hồn.\\r\\n\\r\\nĐặc biệt, ngay gần chùa ngọn núi, nơi bức tượng nàng Tô Thị hóa đá đứng chờ chồng qua suốt hàng trăm năm, gắn liền với câu chuyện dân gian về lòng chung thủy. Đây là một biểu tượng văn hóa độc đáo của Lạng Sơn, thu hút du khách đến khám phá và tìm hiểu.\"},{\"title\":\"Chợ Kỳ Lừa - Giao thoa văn hóa và thương mại\",\"content\":\"Chợ Kỳ Lừa không chỉ là nơi buôn bán nhộn nhịp mà còn là địa điểm du lịch văn hóa đặc sắc. Tại đây, du khách có thể tìm thấy những món ăn đặc sản của Lạng Sơn như vịt quay, phở chua, bánh cuốn trứng, hay những sản phẩm thủ công độc đáo. Đây là nơi lý tưởng để bạn hiểu hơn về cuộc sống và phong cách của người dân địa phương.\"},{\"title\":\"Ải Chi Lăng - Pháo đài lịch sử, mồ chôn giặc phương Bắc.\",\"content\":\"Ải Chi Lăng là một trong những địa danh lịch sử nổi tiếng nhất của Lạng Sơn, gắn liền với những chiến công oanh liệt trong lịch sử đấu tranh bảo vệ đất nước. Nằm giữa hai dãy núi Cai Kinh và núi Bảo Đài, Ải Chi Lăng là một thung lũng hẹp dài khoảng 20km, được xem như cửa ngõ quan trọng ngăn bước quân thù từ phương Bắc tràn xuống.\\r\\n\\r\\nTrong lịch sử, nơi đây đã chứng kiến nhiều trận chiến hào hùng, đặc biệt là trận đánh tiêu diệt quân Minh vào thế kỷ 15 dưới sự lãnh đạo của Lê Lợi. Ải Chi Lăng không chỉ là biểu tượng của lòng yêu nước, sự kiên cường mà còn là minh chứng cho chiến lược quân sự tài tình của cha ông ta. Ngày nay, Ải Chi Lăng là một điểm tham quan lý thú, nơi du khách có thể tìm hiểu về lịch sử oai hùng của dân tộc, đồng thời chiêm ngưỡng khung cảnh thiên nhiên tuyệt đẹp với núi non trùng điệp và không gian yên bình.\"},{\"title\":\"Phần kết.\",\"content\":\"Lạng Sơn không chỉ là một vùng đất giàu lịch sử mà còn là một thiên đường dành cho những ai yêu thích khám phá thiên nhiên và văn hóa. Từ vẻ đẹp hùng vĩ của núi non đến những giá trị văn hóa sâu sắc, Lạng Sơn thực sự là một điểm đến hấp dẫn. Nếu bạn muốn quảng bá hình ảnh quê hương mình, việc nhấn mạnh vào những yếu tố độc đáo trên chắc chắn sẽ thu hút sự quan tâm của nhiều du khách.\"}]', 'public', '74aae3fd-5850-4ff8-97dd-53032a33e7f6', '0b2949f2-ca9b-47d9-97d7-2034fc2bcd0c', '2025-04-01 09:48:41', '2025-04-01 09:48:41', NULL),
('2351e839-6938-42f7-8e79-07ea07412507', 'Hàng xóm của tôi là Totoro: Một kiệt tác của Studio Ghibli', 'upload/images/news/1744311007-2351e839-6938-42f7-8e79-07ea07412507.jpeg', '[{\"title\":\"Giới thiệu\",\"content\":\"                            \\\"Hàng xóm của tôi là Totoro\\\" (My Neighbor Totoro) là một trong những bộ phim hoạt hình kinh điển của Studio Ghibli, được đạo diễn bởi Hayao Miyazaki và ra mắt vào năm 1988. Bộ phim không chỉ là một câu chuyện dành cho trẻ em mà còn mang đến những thông điệp sâu sắc về gia đình, thiên nhiên và tình bạn. Với hình ảnh đẹp mắt và âm nhạc đầy cảm xúc, \\\"Hàng xóm của tôi là Totoro\\\" đã trở thành biểu tượng văn hóa và là nguồn cảm hứng cho nhiều thế hệ khán giả.\\r\\n                        \"},{\"title\":\"Cốt truyện giản dị nhưng đầy ý nghĩa\",\"content\":\"                            Bộ phim kể về hai chị em Satsuki và Mei khi gia đình họ chuyển đến một ngôi làng nhỏ để gần mẹ đang điều trị bệnh. Tại đây, hai cô bé đã khám phá ra khu rừng gần nhà và gặp gỡ Totoro, một sinh vật huyền bí và đáng yêu. Totoro không chỉ là người bạn đồng hành trong những cuộc phiêu lưu mà còn giúp hai chị em vượt qua những khó khăn trong cuộc sống. Câu chuyện tuy đơn giản nhưng chứa đựng những bài học quý giá về tình yêu thương và sự gắn kết gia đình.\\r\\n                        \"},{\"title\":\"Hình ảnh và âm nhạc: Sự kết hợp hoàn hảo\",\"content\":\"                            Hình ảnh trong phim được chăm chút tỉ mỉ với những khung cảnh thiên nhiên tuyệt đẹp, từ cánh đồng xanh mướt đến khu rừng bí ẩn. Totoro và các sinh vật khác như mèo xe buýt (Catbus) được thiết kế độc đáo, tạo nên sự hấp dẫn đặc biệt cho bộ phim. Âm nhạc do Joe Hisaishi sáng tác đã góp phần làm tăng cảm xúc cho từng cảnh quay, từ những giai điệu vui tươi đến những bản nhạc sâu lắng, đầy cảm động.\\r\\n                        \"},{\"title\":\"Thông điệp về thiên nhiên và cuộc sống\",\"content\":\"                            Bộ phim không chỉ là một câu chuyện giải trí mà còn gửi gắm thông điệp về tầm quan trọng của việc bảo vệ thiên nhiên. Totoro và các sinh vật trong rừng đại diện cho sự hòa hợp giữa con người và môi trường. Qua đó, bộ phim khuyến khích khán giả trân trọng và gìn giữ vẻ đẹp tự nhiên xung quanh mình.\\r\\n                        \"},{\"title\":\"Tầm ảnh hưởng và di sản\",\"content\":\"                            Kể từ khi ra mắt, \\\"Hàng xóm của tôi là Totoro\\\" đã nhận được sự yêu mến từ khán giả trên toàn thế giới. Hình ảnh Totoro đã trở thành biểu tượng của Studio Ghibli và xuất hiện trong nhiều sản phẩm văn hóa đại chúng. Bộ phim cũng được đánh giá cao bởi các nhà phê bình và thường xuyên được xếp vào danh sách những bộ phim hoạt hình hay nhất mọi thời đại.\\r\\n                        \"},{\"title\":\"Kết.\",\"content\":\"                            \\\"Hàng xóm của tôi là Totoro\\\" không chỉ là một bộ phim hoạt hình mà còn là một tác phẩm nghệ thuật đầy cảm hứng. Với cốt truyện ý nghĩa, hình ảnh tuyệt đẹp và âm nhạc xuất sắc, bộ phim đã chạm đến trái tim của hàng triệu khán giả. Đây là một tác phẩm không thể bỏ qua đối với những ai yêu thích điện ảnh và muốn tìm kiếm một chút phép màu trong cuộc sống.\\r\\n                        \"}]', 'public', 'bc5d97e8-08eb-11f0-a777-fc3497151179', '10641914-3e5d-4648-abd9-d4b7771998cf', '2025-04-10 18:50:07', '2025-04-10 22:48:12', NULL),
('29663e19-e8bb-40a3-a197-d3bb2cb7d4bb', '\"Nấu Ăn Cho Em\": Bài hát về sự sẻ chia và những câu chuyện phía sau', 'upload/images/news/1743525476-29663e19-e8bb-40a3-a197-d3bb2cb7d4bb.jpg', '[{\"title\":\"Câu chuyện ý nghĩa đằng sau bài hát\",\"content\":\"Bài hát \\\"Nấu Ăn Cho Em\\\" của Đen Vâu, kết hợp cùng giọng ca trong trẻo của PiaLinh, là một tác phẩm âm nhạc giàu cảm xúc, ra đời từ chuyến đi đầy ý nghĩa đến vùng cao Điện Biên. Tại đây, Đen Vâu cùng các thành viên trong đoàn đã chứng kiến cuộc sống đầy khó khăn nhưng tràn đầy nghị lực của các em nhỏ dân tộc thiểu số. Những nụ cười hồn nhiên giữa cảnh thiếu thốn và những bữa ăn đơn sơ đã trở thành nguồn cảm hứng để Đen sáng tác bài hát này.\\r\\n\\r\\nBài hát sử dụng hình ảnh \\\"nấu ăn\\\" không chỉ theo nghĩa đen mà còn tượng trưng cho tình yêu thương, sự sẻ chia và sự gắn bó. Qua ca khúc, Đen Vâu muốn gửi gắm thông điệp rằng việc dành thời gian và tình cảm cho người khác là món quà quý giá nhất mà chúng ta có thể trao tặng.\"},{\"title\":\"Âm nhạc và lời ca đầy cảm xúc\",\"content\":\"Với giai điệu mộc mạc, nhẹ nhàng và phần lời giàu chất tự sự, \\\"Nấu Ăn Cho Em\\\" đã chạm đến trái tim của người nghe. Lời ca không chỉ đơn thuần là những câu chuyện thường ngày mà còn là lời tâm sự đầy chân thành từ góc nhìn của Đen. PiaLinh góp phần làm nổi bật bài hát với chất giọng trong trẻo, mang đến sự cân bằng giữa cảm xúc mạnh mẽ và dịu dàng.\"},{\"title\":\"Dự án ý nghĩa đồng hành\",\"content\":\"Không chỉ dừng lại ở âm nhạc, Đen Vâu cùng với ê-kíp đã thực hiện các dự án thiện nguyện nhằm hỗ trợ trẻ em vùng cao. Họ không chỉ mang đến những bữa ăn, sách vở, mà còn mang hy vọng về một tương lai tốt đẹp hơn cho các em. Những chuyến đi thực tế không chỉ giúp đỡ vật chất mà còn tạo ra những khoảnh khắc gắn bó, làm giàu thêm cảm xúc và ý nghĩa đằng sau bài hát.\\r\\nBài hát cũng là một sự quảng bá đến dự án \\\"Nuôi Em\\\", một dự án thiện nguyện đầy ý nghĩa với mục tiêu cung cấp những bữa ăn đầy đủ cho trẻ em nghèo vùng cao, giúp các em có thêm động lực đến trường. Chính Đen Vâu cũng đã khẳng định 100% doanh thu từ tất cả sản phẩm như MV, Single ở trên tất cả các nền tảng sẽ được chuyển trực tiếp đến dự án \\\"Nuôi Em\\\" - đây là một hành động cực kỳ ý nghĩa khi bài hát không chỉ lan tỏa tinh thần tốt đẹp mà còn mang lại ý nghĩa rất lớn với cả một dự án thiện nguyện và các em nhỏ vùng cao còn nhiều khó khăn, thiếu thốn.\"},{\"title\":\"Phần kết.\",\"content\":\"\\\"Nấu Ăn Cho Em\\\" không chỉ là một bài hát, mà còn là hành trình của sự yêu thương và sẻ chia. Đó là minh chứng cho việc âm nhạc có thể kết nối con người, truyền cảm hứng và lan tỏa những giá trị nhân văn tốt đẹp. Đen Vâu đã một lần nữa khẳng định rằng, âm nhạc không chỉ để nghe mà còn để cảm nhận và hành động.\\r\\nHãy cùng lắng nghe bài hát tại:\\r\\nSpotify: https:\\/\\/open.spotify.com\\/track\\/7td8DTWoGC9u9db37mGHX6?si=6980cf5280d54147\\r\\nYoutube: https:\\/\\/youtu.be\\/ukHK1GVyr0I?si=ZDRx37kPQRGgKNG1\"}]', 'public', 'bc5d80d0-08eb-11f0-a777-fc3497151179', '0b2949f2-ca9b-47d9-97d7-2034fc2bcd0c', '2025-04-01 09:37:56', '2025-04-01 09:37:56', NULL),
('38338c27-965e-497d-8fe2-85f0ba6969b5', 'Cuộc đua Champions League 2024/2025 - Những ứng cử viên sáng giá', 'upload/images/news/1743528907-38338c27-965e-497d-8fe2-85f0ba6969b5.jpg', '[{\"title\":\"Giới thiệu\",\"content\":\"UEFA Champions League (UCL) luôn là giải đấu danh giá nhất của bóng đá châu Âu, nơi các câu lạc bộ hàng đầu tranh tài để giành chiếc cúp bạc danh giá. Mùa giải 2024\\/2025 đã chứng kiến những trận đấu kịch tính và những màn trình diễn xuất sắc từ các đội bóng lớn. Hãy cùng điểm qua những ứng cử viên sáng giá nhất cho danh hiệu năm nay.\"},{\"title\":\"Những ứng cử viên hàng đầu\",\"content\":\"- Manchester City: Với đội hình mạnh mẽ và sự dẫn dắt của Pep Guardiola, Man City tiếp tục là ứng cử viên nặng ký. Họ đã thể hiện phong độ ổn định và khả năng kiểm soát trận đấu vượt trội.\\r\\n\\r\\n- Real Madrid: Đội bóng Hoàng gia Tây Ban Nha luôn là cái tên đáng gờm tại UCL. Với kinh nghiệm và lịch sử vô địch nhiều lần, họ là đối thủ khó chịu cho bất kỳ đội bóng nào.\\r\\n\\r\\n- Bayern Munich: Đại diện nước Đức không chỉ sở hữu đội hình chất lượng mà còn có chiến thuật sắc bén, giúp họ tiến sâu vào giải đấu.\"},{\"title\":\"Những bất ngờ thú vị\",\"content\":\"Ngoài các đội bóng lớn, mùa giải năm nay còn chứng kiến sự nổi lên của những đội bóng ít tên tuổi nhưng đầy tiềm năng như Napoli hay Aston Villa. Sự cạnh tranh khốc liệt giữa các đội bóng đã làm nên một mùa giải đầy hấp dẫn.\"}]', 'public', 'bc5d99b7-08eb-11f0-a777-fc3497151179', '0b2949f2-ca9b-47d9-97d7-2034fc2bcd0c', '2025-04-01 10:35:07', '2025-04-01 10:35:07', NULL),
('3e720970-3e23-44a0-b719-3fee77089f6c', 'Spirited Away: Kiệt tác hoạt hình vượt thời gian của Studio Ghibli', 'upload/images/news/1743521709-3e720970-3e23-44a0-b719-3fee77089f6c.jpg', '[{\"title\":\"Hành trình đầy cảm xúc của Chihiro\",\"content\":\"\\\"Spirited Away\\\" (Sen to Chihiro no Kamikakushi) hay tựa Việt là \\\"Vùng đất linh hồn\\\", được thực hiện bởi Studio Ghibli dưới sự dẫn dắt của đạo diễn huyền thoại Hayao Miyazaki, là một trong những bộ phim hoạt hình Nhật Bản nổi bật nhất mọi thời đại. Bộ phim kể về hành trình của cô bé Chihiro Ogino, người bị mắc kẹt trong thế giới huyền bí sau khi bố mẹ của cô vô tình biến thành lợn bởi tham lam ăn thức ăn của các vị thần. Để cứu bố mẹ và trở về nhà, Chihiro phải vượt qua nhiều thử thách, học cách làm việc trong nhà tắm công cộng của Yubaba - phù thủy cai quản thế giới này.\\r\\nThông qua hành trình đó, Chihiro trưởng thành, tự mình đương đầu với khó khăn, vượt qua sự sợ hãi và khám phá sức mạnh bên trong bản thân. Hành trình của cô bé không chỉ là câu chuyện cá nhân mà còn gợi lên sự liên kết giữa người xem với những giá trị nhân văn sâu sắc.\"},{\"title\":\"Thế giới huyền bí đầy sáng tạo\",\"content\":\"Một trong những điểm sáng nổi bật của \\\"Spirited Away\\\" là sự sáng tạo không giới hạn trong việc xây dựng thế giới. Studio Ghibli đã tạo nên một không gian đầy màu sắc với những sinh vật kỳ diệu như Vô Diện (No-Face), Haku - chàng trai rồng bí ẩn, và các vị thần đa dạng đến nghỉ ngơi tại nhà tắm của Yubaba. Thế giới này không chỉ lôi cuốn bởi vẻ đẹp huyền ảo mà còn chứa đựng những tầng ý nghĩa phức tạp, phản ánh văn hóa, tín ngưỡng và xã hội Nhật Bản.\"},{\"title\":\"Hình ảnh và âm nhạc tuyệt mỹ\",\"content\":\"Không thể không nhắc đến kỹ thuật hình ảnh tuyệt vời của \\\"Spirited Away\\\". Từng khung hình được vẽ tay tinh tế, mang lại cảm giác sống động và giàu cảm xúc. Bộ phim có khả năng làm cho người xem chìm đắm trong từng cảnh tượng, từ những dòng suối thơ mộng, khu rừng ma mị, cho đến nhà tắm đầy sôi động.\\r\\nÂm nhạc do Joe Hisaishi sáng tác là một yếu tố không thể thiếu làm nên sức hút của bộ phim. Các bản nhạc nền không chỉ tạo nên bầu không khí huyền bí mà còn gợi lên cảm xúc mạnh mẽ qua từng khoảnh khắc trong câu chuyện.\"},{\"title\":\"Thông điệp sâu sắc về lòng tham và sự trưởng thành\",\"content\":\"\\\"Spirited Away\\\" không chỉ là một tác phẩm giải trí mà còn gửi gắm những thông điệp mạnh mẽ về lòng tham, sự trưởng thành, và mối quan hệ giữa con người với môi trường xung quanh. Bộ phim khéo léo phê phán sự tiêu thụ vô độ, cách con người ảnh hưởng đến thiên nhiên và sự cần thiết của việc bảo vệ vẻ đẹp tự nhiên. Đồng thời, hành trình của Chihiro cũng là một lời nhắn nhủ về sức mạnh nội tại của con người khi đối diện với khó khăn.\"},{\"title\":\"Tầm ảnh hưởng toàn cầu\",\"content\":\"Từ khi ra mắt vào năm 2001, \\\"Spirited Away\\\" đã nhanh chóng trở thành một hiện tượng quốc tế, giành giải Oscar cho Phim hoạt hình xuất sắc nhất vào năm 2003 và được nhiều khán giả trên thế giới yêu mến. Đây là bộ phim hoạt hình đầu tiên của Nhật Bản đạt được giải thưởng danh giá này, đánh dấu sự công nhận toàn cầu đối với chất lượng và tài năng của Studio Ghibli.\"},{\"title\":\"Phần kết.\",\"content\":\"\\\"Spirited Away\\\" không chỉ là một bộ phim hoạt hình mà còn là một tác phẩm nghệ thuật vượt thời gian, mang lại nhiều tầng cảm xúc và suy ngẫm. Đây là một hành trình đầy cảm hứng, khiến khán giả không thể rời mắt khỏi màn hình, đồng thời nhắc nhở chúng ta về sự cần thiết của lòng nhân ái và sự sẻ chia.\"}]', 'public', 'bc5d97e8-08eb-11f0-a777-fc3497151179', '0b2949f2-ca9b-47d9-97d7-2034fc2bcd0c', '2025-04-01 08:35:09', '2025-04-01 08:35:09', NULL),
('4a75763e-69cd-4441-96bb-78e02ff1ea6a', 'Test Xem dang dc khgon', 'upload/images/news/1743439969-4a75763e-69cd-4441-96bb-78e02ff1ea6a.png', '[{\"title\":\"Tiêu đề đoạn văn 1 cho bài báo 1\",\"content\":\"wwwwwwwwwwwwwwwwwwwwwwwww\"}]', 'deleted', 'bc5d97e8-08eb-11f0-a777-fc3497151179', '0b2949f2-ca9b-47d9-97d7-2034fc2bcd0c', '2025-03-31 09:52:49', '2025-03-31 13:07:14', '2025-03-31 13:07:14'),
('555fe87e-1f1a-4ad2-b120-2c49e65d21bc', 'Doraemon: Hành trình vượt thời gian của chú mèo máy từ tương lai', 'upload/images/news/1744325266-555fe87e-1f1a-4ad2-b120-2c49e65d21bc.png', '[{\"title\":\"Mở đầu\",\"content\":\"                            Doraemon, một trong những biểu tượng văn hóa nổi bật của Nhật Bản, là bộ truyện tranh được sáng tác bởi Fujiko F. Fujio. Ra đời vào năm 1969, Doraemon đã nhanh chóng trở thành một phần không thể thiếu trong tuổi thơ của hàng triệu độc giả trên toàn thế giới. Với cốt truyện hấp dẫn, nhân vật đáng yêu và thông điệp sâu sắc, bộ truyện không chỉ mang tính giải trí mà còn truyền tải những giá trị nhân văn về tình bạn, gia đình và sự sáng tạo.\\r\\n                        \"},{\"title\":\"Cốt truyện: Cuộc sống của Nobita và Doraemon\",\"content\":\"                            Doraemon kể về cuộc sống của Nobita, một cậu bé hậu đậu, thường xuyên gặp rắc rối trong học tập và cuộc sống. Để giúp Nobita cải thiện tương lai, con cháu của cậu đã gửi Doraemon, một chú mèo máy đến từ thế kỷ 22, quay ngược thời gian để hỗ trợ. Doraemon sở hữu chiếc túi thần kỳ chứa đầy những bảo bối độc đáo, giúp Nobita vượt qua khó khăn. Tuy nhiên, những bảo bối này thường dẫn đến những tình huống hài hước và đôi khi là rắc rối lớn hơn. Cốt truyện không chỉ mang lại tiếng cười mà còn khơi gợi sự đồng cảm và bài học quý giá về cuộc sống.\\r\\n                        \"},{\"title\":\"Nhân vật: Sự đa dạng và độc đáo\",\"content\":\"                            Bên cạnh Doraemon và Nobita, bộ truyện còn có sự góp mặt của nhiều nhân vật phụ thú vị như Shizuka, Gian và Suneo. Shizuka là cô bé hiền lành, tốt bụng, luôn là người bạn đồng hành đáng tin cậy của Nobita. Gian, với tính cách mạnh mẽ nhưng đôi khi hung hăng, lại mang đến những tình huống hài hước và cảm động. Suneo, cậu bé thích khoe khoang, thường tạo ra những mâu thuẫn nhưng cũng có những khoảnh khắc đáng yêu. Sự đa dạng trong tính cách của các nhân vật đã tạo nên một thế giới sống động và gần gũi.\\r\\n                        \"},{\"title\":\"Những bảo bối thần kỳ: Sáng tạo không giới hạn\",\"content\":\"                            Một trong những yếu tố làm nên sức hút của Doraemon chính là những bảo bối thần kỳ từ chiếc túi thần kỳ của chú mèo máy. Từ cánh cửa thần kỳ, chong chóng tre, đến bánh mì ghi nhớ, mỗi bảo bối đều mang đến những tình huống thú vị và bất ngờ. Những bảo bối này không chỉ là công cụ giúp Nobita vượt qua khó khăn mà còn là biểu tượng của sự sáng tạo và trí tưởng tượng không giới hạn.\\r\\n                        \"},{\"title\":\"Thông điệp sâu sắc: Tình bạn và sự trưởng thành\",\"content\":\"                            Doraemon không chỉ là một bộ truyện tranh giải trí mà còn chứa đựng những thông điệp sâu sắc về tình bạn, gia đình và sự trưởng thành. Mối quan hệ giữa Nobita và Doraemon là minh chứng cho sự gắn kết và hỗ trợ lẫn nhau trong cuộc sống. Những bài học về lòng dũng cảm, sự kiên trì và trách nhiệm được lồng ghép một cách khéo léo, giúp độc giả, đặc biệt là trẻ em, học hỏi và phát triển.\\r\\n                        \"},{\"title\":\"Tầm ảnh hưởng và di sản\",\"content\":\"                            Kể từ khi ra mắt, Doraemon đã trở thành một hiện tượng toàn cầu, được dịch ra nhiều ngôn ngữ và chuyển thể thành anime, phim điện ảnh, trò chơi điện tử và nhiều loại hình truyền thông khác. Hình ảnh Doraemon đã trở thành biểu tượng của sự sáng tạo và niềm vui, gắn liền với tuổi thơ của nhiều thế hệ. Bộ truyện không chỉ là một tác phẩm nghệ thuật mà còn là nguồn cảm hứng cho sự sáng tạo và khám phá.\\r\\n                        \"},{\"title\":\"Sẽ không có kết.\",\"content\":\"                            Doraemon không chỉ là một bộ truyện tranh mà còn là một phần ký ức tuổi thơ của hàng triệu người trên khắp thế giới. Với cốt truyện hấp dẫn, nhân vật đáng yêu và thông điệp sâu sắc, Doraemon đã chạm đến trái tim của độc giả và trở thành một biểu tượng văn hóa không thể thay thế. Đây là một tác phẩm mà bất kỳ ai yêu thích truyện tranh đều không thể bỏ qua.\\r\\n                        \"}]', 'public', 'bc5d9a2f-08eb-11f0-a777-fc3497151179', 'd8c80e08-4383-4fa2-83d7-48fb98dfc27f', '2025-04-10 19:11:11', '2025-04-11 17:59:12', NULL),
('5ab1e9e9-7cf9-4ff4-b074-ad8ca2fedadc', 'Doraemon Stand By Me 2: Một câu chuyện cảm xúc về gia đình và tình yêu', 'upload/images/news/1743523992-5ab1e9e9-7cf9-4ff4-b074-ad8ca2fedadc.jpg', '[{\"title\":\"Cốt truyện đầy ý nghĩa\",\"content\":\"\\\"Doraemon Stand By Me 2\\\" là phần tiếp theo của bộ phim hoạt hình 3D nổi tiếng \\\"Stand By Me Doraemon\\\". Bộ phim tiếp tục khai thác những câu chuyện cảm động xoay quanh Nobita và Doraemon, nhưng lần này tập trung vào mối quan hệ gia đình và tình yêu. Nobita quyết định quay ngược thời gian để gặp lại bà của mình, người đã từng chăm sóc cậu khi còn nhỏ. Bà của Nobita bày tỏ mong muốn được thấy cô dâu của cậu, và từ đó, hành trình đầy cảm xúc bắt đầu.\\r\\n\\r\\nTrong khi đó, Nobita trưởng thành lại đối mặt với sự lo lắng và áp lực về việc trở thành một người chồng tốt cho Shizuka. Bộ phim không chỉ là câu chuyện về tình bạn mà còn là hành trình khám phá giá trị của gia đình và sự trưởng thành.\"},{\"title\":\"Hình ảnh và kỹ thuật ấn tượng\",\"content\":\"Giống như phần đầu, \\\"Stand By Me Doraemon 2\\\" tiếp tục gây ấn tượng với đồ họa 3D đẹp mắt và chi tiết. Các cảnh quay từ quá khứ đến tương lai được tái hiện sống động, mang lại cảm giác chân thực và đầy cảm xúc. Âm nhạc trong phim cũng được chăm chút kỹ lưỡng, góp phần làm nổi bật những khoảnh khắc quan trọng trong câu chuyện.\"},{\"title\":\"Kết.\",\"content\":\"Bộ phim gửi gắm thông điệp về tình yêu thương gia đình, sự hy sinh và trách nhiệm. Nó nhắc nhở chúng ta rằng, dù thời gian có trôi qua, những giá trị cốt lõi như tình yêu và sự gắn bó vẫn luôn là điều quan trọng nhất.\\r\\n\\\"Doraemon Stand By Me 2\\\" là một tác phẩm không chỉ dành cho trẻ em mà còn cho cả người lớn, những ai đã từng lớn lên cùng Doraemon. Bộ phim mang lại tiếng cười, nước mắt và những bài học quý giá về cuộc sống.\"}]', 'public', 'bc5d97e8-08eb-11f0-a777-fc3497151179', '0b2949f2-ca9b-47d9-97d7-2034fc2bcd0c', '2025-04-01 09:13:12', '2025-04-01 09:13:12', NULL),
('6fb40687-b696-4297-a80a-297e51d25737', 'Tứ kết lượt đi UEFA Champions League: Những trận đấu đầy kịch tính', 'upload/images/news/1744308314-6fb40687-b696-4297-a80a-297e51d25737.png', '[{\"title\":\"Mở đầu\",\"content\":\"                        Lượt đi tứ kết UEFA Champions League mùa giải năm nay đã mang đến những cuộc đối đầu không thể bỏ lỡ giữa các đội bóng hàng đầu châu Âu. Các trận đấu không chỉ đầy hấp dẫn với những pha bóng đỉnh cao mà còn chứa đựng nhiều bất ngờ khi các đội cạnh tranh quyết liệt cho chiếc vé vào bán kết. Hãy cùng điểm lại kết quả và những diễn biến đáng chú ý nhất sau loạt trận này.\\r\\n                    \"},{\"title\":\"Arsenal áp đảo Real Madrid\",\"content\":\"                        Arsenal đã có màn trình diễn xuất sắc khi đánh bại Real Madrid với tỷ số 3-0 ngay tại sân Emirates. Declan Rice trở thành ngôi sao sáng với hai bàn thắng từ những cú đá phạt trực tiếp đầy kỹ thuật, khiến thủ môn Courtois bất lực. Bàn thắng thứ ba của Arsenal được ghi bởi Mikel Merino, khép lại một trận đấu hoàn hảo cho đội bóng nước Anh. Với lợi thế lớn này, Arsenal đang tiến gần hơn đến vòng bán kết.\\r\\n                    \"},{\"title\":\"Barcelona hủy diệt Dortmund\",\"content\":\"                        Barcelona đã thể hiện sức mạnh vượt trội khi giành chiến thắng 4-0 trước Borussia Dortmund tại sân nhà Olímpic Lluís Companys. Bộ ba Raphinha, Robert Lewandowski và Lamine Yamal đồng loạt tỏa sáng, ghi bàn và tạo nên thế trận áp đảo. Với tỷ lệ kiểm soát bóng lên đến 61%, Barcelona đã chứng minh họ là ứng cử viên hàng đầu cho danh hiệu vô địch mùa giải năm nay.\\r\\n                    \"},{\"title\":\"PSG ngược dòng trước Aston Villa\",\"content\":\"                        Paris Saint-Germain đã có màn ngược dòng ấn tượng khi đánh bại Aston Villa với tỷ số 3-1 tại Parc des Princes. Sau khi bị dẫn trước bởi bàn thắng của Morgan Rogers, PSG nhanh chóng lấy lại thế trận nhờ công của Désiré Doué. Sang hiệp hai, Khvicha Kvaratskhelia và Nuno Mendes lần lượt ghi bàn, giúp đội bóng nước Pháp giành lợi thế lớn trước trận lượt về.\\r\\n                    \"},{\"title\":\"Inter Milan vượt qua Bayern Munich\",\"content\":\"                        Inter Milan đã có chiến thắng quan trọng 2-1 trước Bayern Munich ngay tại Allianz Arena. Với bàn thắng quyết định của Lautaro Martínez, Inter đã tạo ra lợi thế lớn trước trận lượt về trên sân nhà Giuseppe Meazza. Bayern Munich sẽ cần một màn trình diễn xuất sắc để lật ngược tình thế.\\r\\n                    \"},{\"title\":\"Kết.\",\"content\":\"                        Lượt đi tứ kết UEFA Champions League đã mang đến những trận cầu nảy lửa cùng nhiều cung bậc cảm xúc cho người hâm mộ toàn thế giới. Từ những chiến thắng đậm đà cho đến những màn ngược dòng ngoạn mục, các đội bóng đã chứng minh đẳng cấp và khát khao của mình trên hành trình chinh phục danh hiệu cao quý nhất châu Âu. Trận lượt về hứa hẹn sẽ còn hấp dẫn hơn nữa khi các đội tiếp tục cạnh tranh quyết liệt để giành chiếc vé bước vào vòng bán kết\\r\\n                    \"}]', 'pending', 'bc5d99b7-08eb-11f0-a777-fc3497151179', '10641914-3e5d-4648-abd9-d4b7771998cf', '2025-04-10 18:05:15', '2025-04-11 17:57:50', NULL),
('82fe6b03-115b-4909-9574-6336134bf7e0', 'Midnights: Album dẫn lối vào những tâm tư giữa đêm muộn', 'upload/images/news/1743524345-82fe6b03-115b-4909-9574-6336134bf7e0.png', '[{\"title\":\"Khám phá ý tưởng độc đáo của album\",\"content\":\"                \\\"Midnights\\\", phát hành vào năm 2022, là album phòng thu thứ 10 của Taylor Swift, đánh dấu một bước đi đầy sáng tạo và cảm xúc. Được giới thiệu như một chuyến hành trình xuyên qua những suy nghĩ vào lúc nửa đêm, album là sự kết hợp của những câu chuyện, giấc mơ, và những giờ suy tư khiến chúng ta cảm thấy vừa cô đơn vừa gắn kết.\\r\\n\\r\\nTaylor Swift đã mô tả \\\"Midnights\\\" là bộ sưu tập gồm \\\"13 câu chuyện mất ngủ\\\", nơi cô khám phá nhiều trạng thái cảm xúc khác nhau – từ sự tự ti, lo lắng đến tình yêu và niềm hy vọng. Album là sự pha trộn giữa các yếu tố synth-pop, electro, và lời ca đầy mê hoặc, thể hiện sự trưởng thành và phong cách ngày càng đa dạng của nữ nghệ sĩ.\\r\\n            \"},{\"title\":\"Các ca khúc nổi bật trong album\",\"content\":\"                \\\"Anti-Hero\\\": Là đĩa đơn chủ đạo của album, \\\"Anti-Hero\\\" mang một giai điệu synth-pop bắt tai cùng lời tự sự sâu sắc. Taylor dùng bài hát để thừa nhận những khuyết điểm và bất an của chính mình, tạo sự đồng cảm mạnh mẽ với khán giả.\\r\\n\\r\\n\\\"Lavender Haze\\\": Mở đầu album với âm hưởng mơ màng, ca khúc này thể hiện khía cạnh lãng mạn và mong muốn thoát khỏi những định kiến xã hội trong tình yêu.\\r\\n\\r\\n\\\"Bejeweled\\\": Một bản nhạc vui tươi, ca ngợi sự tự tin và tỏa sáng của chính bản thân mỗi con người.\\r\\n\\r\\n\\\"Karma\\\": Một bài hát độc đáo với lời ca mang tính chơi đùa nhưng đầy ngụ ý, nhấn mạnh ý tưởng rằng \\\"luật nhân quả\\\" luôn hiện diện.\\r\\n\\r\\n\\\"Midnight Rain\\\": Đưa khán giả vào một không gian âm nhạc đầy hoài niệm, bài hát xoay quanh những cảm xúc phức tạp về tình yêu và sự thay đổi.\\r\\n            \"},{\"title\":\"Hình ảnh và sản xuất của album\",\"content\":\"                Taylor Swift và nhóm sản xuất, trong đó có Jack Antonoff - người cộng sự quen thuộc của cô, đã xây dựng nên một không gian âm nhạc đầy mê hoặc, hòa quyện giữa âm thanh synth hiện đại và chất trữ tình tinh tế. Album không chỉ gây ấn tượng về âm nhạc mà còn qua hình ảnh đậm tính thẩm mỹ: từ bìa album với tông màu trầm ấm, biểu tượng của những phút suy tư trong đêm, đến các video âm nhạc được đầu tư công phu.\\r\\n            \"},{\"title\":\"Thông điệp sâu sắc và sức ảnh hưởng\",\"content\":\"                \\\"Midnights\\\" không chỉ đơn thuần là một album âm nhạc mà còn là một tác phẩm nghệ thuật khám phá tâm tư con người. Qua những lời ca khúc giàu tính tự sự, Taylor Swift đã mở ra một cánh cửa để khán giả bước vào thế giới nội tâm của mình.\\r\\n\\r\\nNgay khi phát hành, \\\"Midnights\\\" đã đạt được những thành tích ấn tượng, phá vỡ nhiều kỷ lục trên các bảng xếp hạng, đồng thời củng cố vị thế của Taylor Swift như một trong những nghệ sĩ hàng đầu của thế kỷ XXI.\\r\\n            \"}]', 'public', 'bc5d80d0-08eb-11f0-a777-fc3497151179', '0b2949f2-ca9b-47d9-97d7-2034fc2bcd0c', '2025-04-01 15:38:20', '2025-04-02 18:27:32', NULL),
('854fb205-c5a3-419b-be38-17926e0b5e73', 'Tác động của UCL đến bóng đá châu Âu', 'upload/images/news/1743529127-854fb205-c5a3-419b-be38-17926e0b5e73.png', '[{\"title\":\"Giới thiệu\",\"content\":\"UEFA Champions League không chỉ là một giải đấu mà còn là một biểu tượng của bóng đá châu Âu. Tác động của UCL không chỉ dừng lại ở sân cỏ mà còn lan tỏa đến các khía cạnh kinh tế, văn hóa và xã hội.\"},{\"title\":\"Tác động mạnh mẽ đến bóng đá châu Âu\",\"content\":\"Tác động kinh tế:\\r\\nUCL mang lại nguồn thu khổng lồ cho các câu lạc bộ tham gia, từ tiền thưởng đến doanh thu bán vé và bản quyền truyền hình. Điều này giúp các đội bóng đầu tư mạnh mẽ vào đội hình và cơ sở vật chất.\\r\\n\\r\\nTác động văn hóa:\\r\\nChampions League là nơi giao thoa văn hóa giữa các quốc gia, khi người hâm mộ từ khắp nơi trên thế giới cùng cổ vũ cho đội bóng yêu thích. Đây cũng là cơ hội để các cầu thủ trẻ thể hiện tài năng và trở thành biểu tượng toàn cầu.\\r\\n\\r\\nTác động xã hội:\\r\\nUCL không chỉ là sân chơi của các đội bóng lớn mà còn là nguồn cảm hứng cho hàng triệu người hâm mộ. Những câu chuyện về sự nỗ lực và thành công tại UCL đã truyền cảm hứng cho thế hệ trẻ theo đuổi đam mê bóng đá.\"}]', 'public', 'bc5d99b7-08eb-11f0-a777-fc3497151179', '0b2949f2-ca9b-47d9-97d7-2034fc2bcd0c', '2025-04-01 10:38:47', '2025-04-01 10:38:47', NULL),
('b6b94c6f-e292-4478-a6f9-84ba4eededf5', 'Đây là bài báo chạy thử', 'upload/images/news/1744237390-b6b94c6f-e292-4478-a6f9-84ba4eededf5.jpeg', '[{\"title\":\"Đây là đoạn văn đầu tiên\",\"content\":\"                111111111111111111111111111111\\r\\n            \"},{\"title\":\"Đây là đoạn văn Thứ 2\",\"content\":\"                222222222222222222\\r\\n            \"}]', 'deleted', '32257766-2d1d-4d76-a1ac-e6bce75f3780', '0b2949f2-ca9b-47d9-97d7-2034fc2bcd0c', '2025-04-09 22:23:10', '2025-04-09 22:25:36', '2025-04-09 22:25:36'),
